"use strict";var btns=document.querySelectorAll(".button"),modalContainer=document.querySelector(".modal-container"),tabs=document.querySelector(".tabs"),info=document.querySelector(".info");modalContainer.addEventListener("click",function(t){btns.forEach(function(t){t.classList.remove("active-btn")}),t.target.classList.add("active-btn"),t.target.classList.contains("first")?info.innerText="First tab got em like wat":t.target.classList.contains("second")?info.innerText="Second tab is lit":info.innerText="Third tab's is where da homies at"});